sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var com = g_form.getValue('company_new');
	//alert(com);


	if(com === 'aanoor_global'){
		g_form.clearOptions('u_invoice_item');
		g_form.addOption('u_invoice_item','Copyright Registration','Copyright Registration','1');
		g_form.addOption('u_invoice_list','CopyRight Registration. - 2','CopyRight Registration. - 2','2');
	}

	else if(com == 'aanoor_efilings'){
		g_form.clearOptions('u_invoice_item');
		g_form.addOption('u_voice_item','ESI Filing','ESI Filing');

	}

   //Type appropriate comment here, and begin script below

}
